(250, '_transient_woocommerce_addons_data', 'O:8:"stdClass":10:{s:8:"featured";a:4:{i:0;O:8:"stdClass":5:{s:5:"title";s:20:"WooCommerce Bookings";s:5:"image";s:0:"";s:7:"excerpt";s:108:"Sell your time or date based bookings! Perfect for those wanting to offer appointments, services or rentals.";s:4:"link";s:114:"http://www.woothemes.com/products/woocommerce-bookings/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;249.00";}i:1;O:8:"stdClass":5:{s:5:"title";s:15:"Product Add-ons";s:5:"image";s:0:"";s:7:"excerpt";s:110:"Allow your customers to customise your products by adding input boxes, dropdowns or a field set of checkboxes.";s:4:"link";s:109:"http://www.woothemes.com/products/product-add-ons/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:10:"&#36;49.00";}i:2;O:8:"stdClass":5:{s:5:"title";s:25:"WooCommerce Subscriptions";s:5:"image";s:84:"https://www.woothemes.com/wp-content/uploads/woocommerce_uploads/2012/09/woosubs.png";s:7:"excerpt";s:85:"WC Subscriptions makes it easy to create and manage products with recurring payments.";s:4:"link";s:119:"http://www.woothemes.com/products/woocommerce-subscriptions/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}i:3;O:8:"stdClass":5:{s:5:"title";s:19:"Table Rate Shipping";s:5:"image";s:0:"";s:7:"excerpt";s:123:"Advanced, flexible shipping. Define multiple shipping rates based on location, price, weight, shipping class or item count.";s:4:"link";s:115:"http://www.woothemes.com/products/table-rate-shipping-2/?utm_source=product&utm_medium=upsell&utm_campaign=wcaddons";s:5:"price";s:11:"&#36;199.00";}}s:7:"popular";a:25:{i:0;O:8:"stdClass":5:{s:5:"title";s:19:"WooCommerce iOS App";s:5:"image";s:0:"";s:7:"excerpt";s:217:"Keep your finger on the pulse of your online shop with WooCommerce iOS. All of your shop''s catalog & performance reports are now just a tap away - quickly accessible & beautifully presented right there on